// src/EditScene.js
import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // Importa useNavigate invece di useHistory
import './Scene.css';

const EditScene = () => {
  const { title } = useParams(); // Parametro URL per l'ID della scena
  const navigate = useNavigate(); // Utilizza useNavigate per la navigazione

  const [description, setDescription] = useState('');

  useEffect(() => {
    // Recupera i dettagli della scena dal server
    fetch(`http://localhost:5000/scenes/${title}`)
      .then(response => response.json())
      .then(data => {
        setDescription(data.description);
      })
      .catch(error => {
        console.error('Error fetching scene details:', error);
        // Gestione dell'errore o reindirizzamento ad esempio
      });
  }, [id]);

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleSave = (e) => {
    e.preventDefault();

    const sceneData = {
      description: description,
    };

    // Invia la richiesta PUT per aggiornare la descrizione della scena
    fetch(`http://localhost:5000/scenes/update/${title}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(sceneData),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok ' + response.statusText);
        }
        return response.json();
      })
      .then(data => {
        console.log('Success:', data);
        alert('Descrizione della scena aggiornata con successo!');
        // Reindirizzamento alla pagina principale o ad altre azioni
        // Esempio di reindirizzamento alla home dopo l'aggiornamento
        navigate('/'); // Utilizza navigate per reindirizzare
      })
      .catch((error) => {
        console.error('Error:', error);
        alert('Errore nell\'aggiornamento della descrizione della scena');
      });
  };

  return (
    <div className="scene-container">
      <h1>Modifica della scena</h1>
      <form className="scene-form" onSubmit={handleSave}>
        <div className="form-group">
          <label htmlFor="description">Descrizione:</label>
          <textarea
            id="description"
            value={description}
            onChange={handleDescriptionChange}
            required
            readOnly={false} // Abilita la modifica della descrizione
          ></textarea>
        </div>
        <button type="submit" className="create-button">Salva Modifiche</button>
      </form>
    </div>
  );
};

export default EditScene;
