// src/components/EditPage.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './EditPage.css'; // Stili CSS per il componente

const EditPage = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [sceneType, setSceneType] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    // Simulazione del recupero dei dati della scena dal server
    // Nella realtà, sostituisci con una chiamata effettiva al backend
    fetchSceneData(); // Chiamata iniziale per caricare i dati

    // Funzione per recuperare i dati della scena dal server (simulazione)
    function fetchSceneData() {
      // Supponiamo che i dati siano disponibili in un oggetto JSON
      const mockSceneData = {
        title: 'Titolo della Scena',
        description: 'Descrizione della scena che può essere modificata',
        sceneType: 'Tipo di Scena',
      };

      // Aggiorna lo stato con i dati recuperati
      setTitle(mockSceneData.title);
      setDescription(mockSceneData.description);
      setSceneType(mockSceneData.sceneType);
    }
  }, []);

  const handleDescriptionChange = (e) => {
    setDescription(e.target.value);
  };

  const handleUpdate = () => {
    // Simula l'aggiornamento della descrizione sul server (PUT request)
    // Nella realtà, sostituisci con una chiamata effettiva al backend
    console.log('Descrizione aggiornata:', description);
    alert('Descrizione aggiornata con successo!');
  };

  const handleLogout = () => {
    // Gestione del logout
    // Puoi implementare la logica per il logout qui, come rimuovere il token JWT dal localStorage, ecc.
    navigate('/login'); // Reindirizza alla pagina di login dopo il logout
  };

  return (
    <div className="edit-page">
      <header className="edit-header">
        <h1>Edit Page</h1>
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </header>
      <div className="edit-content">
        <div className="form-group">
          <label htmlFor="title">Titolo:</label>
          <input type="text" id="title" value={title} readOnly />
        </div>
        <div className="form-group">
          <label htmlFor="description">Descrizione:</label>
          <textarea
            id="description"
            value={description}
            onChange={handleDescriptionChange}
            required
          ></textarea>
        </div>
        <div className="form-group">
          <label htmlFor="sceneType">Tipo di Scena:</label>
          <input type="text" id="sceneType" value={sceneType} readOnly />
        </div>
        <button onClick={handleUpdate} className="update-button">Aggiorna</button>
      </div>
    </div>
  );
};

export default EditPage;
