// src/SceneList.js
import React, { useState, useEffect } from 'react';
import './SceneList.css';

const SceneList = () => {
  const [scenes, setScenes] = useState([]);
  const [editIndex, setEditIndex] = useState(null);
  const [newDescription, setNewDescription] = useState('');

  useEffect(() => {
    // Recupera tutte le scene dal server
    fetch('http://localhost:5000/scenes')
      .then(response => response.json())
      .then(data => setScenes(data))
      .catch(error => console.error('Error fetching scenes:', error));
  }, []);

  const handleEditClick = (index, description) => {
    setEditIndex(index);
    setNewDescription(description);
  };

  const handleDescriptionChange = (e) => {
    setNewDescription(e.target.value);
  };

  const handleSaveClick = (title) => {
    // Invia la richiesta PUT per aggiornare la descrizione della scena
    fetch(`http://localhost:5000/scenes/update/${title}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ description: newDescription }),
    })
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(() => {
        // Aggiorna la descrizione localmente
        const updatedScenes = scenes.map((scene, index) =>
          index === editIndex ? { ...scene, description: newDescription } : scene
        );
        setScenes(updatedScenes);
        setEditIndex(null);
        setNewDescription('');
      })
      .catch(error => console.error('Error updating scene:', error));
  };

  return (
    <div className="scene-list-container">
      <h1>Lista delle Scene</h1>
      {scenes.map((scene, index) => (
        <div key={scene._id} className="scene-item">
          <h2>{scene.title}</h2>
          {editIndex === index ? (
            <div>
              <textarea
                value={newDescription}
                onChange={handleDescriptionChange}
              />
              <button onClick={() => handleSaveClick(scene.title)}>Salva</button>
            </div>
          ) : (
            <p>{scene.description}</p>
          )}
          <button onClick={() => handleEditClick(index, scene.description)}>Modifica</button>
        </div>
      ))}
    </div>
  );
};

export default SceneList;
